INSERT INTO `sanpham` (`idsp`, `tensp`, `iddm`, `masp`, `img`, `hangsp`, `thongtin`, `baiviet`, `ngonngu`, `noibat`, `link_title`) VALUES
(1, 'GIƯỜNG BỆNH NHÂN', 4, 'AG-BMS101B', '20170510202818-20160601143153_ag-bms101a.jpg', 'Trung Quốc', '<div><span style="font-family: Arial, Helvetica, sans-serif;"><span style="font-size: 12px;">readmore</span></span></div>\n<ul style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">\n<li>K&iacute;ch thước: 2150 x 950 x 550mm</li>\n<li>Đầu giường bằng tấm ABS.</li>\n<li>Be giường bằng hợp kim nh&ocirc;m.</li>\n<li>D&aacute;t giường bằng th&eacute;p lưới.</li>\n<li>B&aacute;nh xe phanh h&atilde;m ch&eacute;o.</li>\n</ul>', '<ul style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">\r\n<li>K&iacute;ch thước: 2150 x 950 x 550mm</li>\r\n<li>Đầu giường bằng tấm ABS.</li>\r\n<li>Be giường bằng hợp kim nh&ocirc;m.</li>\r\n<li>D&aacute;t giường bằng th&eacute;p lưới.</li>\r\n<li>B&aacute;nh xe phanh h&atilde;m ch&eacute;o.</li>\r\n</ul>', 0, NULL, ''),
(2, 'Cân trọng lượng có thước đo chiều cao', 3, 'Tz-120', '20170510182823-can-suc-khoe-tz-120.jpg', ' Thượng Hải - Trung Quốc', '<p style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">readmore</p>\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">Trọng lượng c&acirc;n tối đa: &nbsp; &nbsp;120 Kg</p>\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">Vạch chia nhỏ nhất: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 0,5 Kg</p>\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">Đo chiều cao:</p>\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">Giới hạn đo: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 190 cm</p>\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">Vạch chia nhỏ nhất: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 0,5 cm</p>\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">K&iacute;ch thước b&agrave;n c&acirc;n: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 280 x 380 mm</p>\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">K&iacute;ch thước tổng thể: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 950 x 300 x 290 mm</p>\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">Trọng lượng: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;15 Kg</p>', '<p style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">Với ti&ecirc;u chuẩn đo chiều cao, c&acirc;n sức khoẻ loại n&agrave;y c&oacute; 2 chức năng, ta c&oacute; thể d&ugrave;ng n&oacute; như sau:</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">1. Với hai b&aacute;nh xe nhỏ, c&acirc;n c&oacute; thể đẩy di chuyển được.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">2. Bằng việc mở c&acirc;n, lật th&acirc;n dựng đứng l&ecirc;n 1800 chốt c&aacute;c chốt h&atilde;m v&agrave; đặt c&aacute;c dao c&acirc;n hợp l&yacute; ta đ&atilde; c&oacute; c&acirc;n sẵn s&agrave;ng hoạt động.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">3. Kim chỉ vạch c&acirc;n bắt đầu phải ở vị tr&iacute; số 0, nếu chưa được như vậy phải chỉnh v&iacute;t chỉnh ở ph&iacute;a dưới đồng hồ.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">4. Khi đo chiều cao thước đo được k&eacute;o ra ở ph&iacute;a b&ecirc;n phải, ống thang đo b&ecirc;n trong ghi từ 70 + 126 cm, gi&aacute; trị đọc bao gồm trị số chuẩn v&agrave; trị số của thang 126 cm, mức chiều cao vượt qu&aacute; 126 cm, r&uacute;t tiếp thang đo thứ 3.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">5. Khi dao treo c&acirc;n v&agrave; thớt (gi&aacute; đỡ dao) kh&ocirc;ng ăn v&agrave;o nhau, mở b&agrave;n c&acirc;n chỉnh cho 4 dao lu&ocirc;n ăn đ&uacute;ng khớp dao. C&oacute; 4 bộ dao v&agrave; thớt ở b&agrave;n c&acirc;n để treo c&acirc;n.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">6. Trong trường hợp kh&ocirc;ng chỉnh được vị tr&iacute; "0" của c&acirc;n bằng v&iacute;t chỉnh "0" ph&iacute;a dưới đồng hồ bắt buộc phải th&aacute;o mặt k&iacute;nh đồng hồ, th&aacute;o 2 v&iacute;t bắt mạch thang đo rồi xoay cả mặt thang đo n&agrave;y về vị tr&iacute; hợp l&yacute;. Phần vi chỉnh ta lại d&ugrave;ng v&iacute;t vi chỉnh ph&iacute;a dưới mặt đồng hồ để đạt vị tr&iacute; "0" chuẩn x&aacute;c.</p>', 0, NULL, ''),
(3, 'GIƯỜNG BỆNH NHÂN', 4, 'AG-BM101', '20170510185140-giuong-benh-nhan.jpg', 'Trung Quốc', '<div><span style="font-family: Arial, Helvetica, sans-serif;"><span style="font-size: 12px;">readmore</span></span></div>\n<ul style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">\n<li>K&iacute;ch thước: 2150 x 980 x (460-750)mm</li>\n<li>Đầu giường v&agrave; be giường bằng tấm ABS.</li>\n<li>D&aacute;t giường bằng th&eacute;p.</li>\n<li>3 chức năng.</li>\n<li>Động cơ: L&amp;K.</li>\n</ul>', '<ul style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">\r\n<li>K&iacute;ch thước: 2150 x 980 x (460-750)mm</li>\r\n<li>Đầu giường v&agrave; be giường bằng tấm ABS.</li>\r\n<li>D&aacute;t giường bằng th&eacute;p.</li>\r\n<li>3 chức năng.</li>\r\n<li>Động cơ: L&amp;K.</li>\r\n</ul>', 0, NULL, ''),
(4, 'GIƯỜNG BỆNH NHÂN', 3, 'AG-BMS002', '20170510185348-giuong-benh-nhan_ag-bms002.jpg', 'Trung Quốc', '<div><span style="font-family: Arial, Helvetica, sans-serif;"><span style="font-size: 12px;">readmore</span></span></div>\n<ul style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">\n<li>K&iacute;ch thước: 2150 x 950 x (460-750)mm</li>\n<li>Đầu giường bằng tấm ABS.</li>\n<li>Be giường bằng hợp kim nh&ocirc;m.</li>\n<li>D&aacute;t giường bằng th&eacute;p.</li>\n<li>B&aacute;nh xe phanh điều khiển trung t&acirc;m</li>\n</ul>', '<ul style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">\r\n<li>K&iacute;ch thước: 2150 x 950 x (460-750)mm</li>\r\n<li>Đầu giường bằng tấm ABS.</li>\r\n<li>Be giường bằng hợp kim nh&ocirc;m.</li>\r\n<li>D&aacute;t giường bằng th&eacute;p.</li>\r\n<li>B&aacute;nh xe phanh điều khiển trung t&acirc;m</li>\r\n</ul>', 0, NULL, ''),
(5, 'GIƯỜNG BỆNH NHÂN', 4, 'B-10', '20170510185533-20160601143401_b-10.jpg', 'Trung Quốc', '<div><span style="font-family: Arial, Helvetica, sans-serif;"><span style="font-size: 12px;">readmore</span></span></div>\n<ul style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">\n<li>K&iacute;ch thước: 2150 x 900 x 520-720mm (DxRxC)</li>\n<li>Giường Elit Semi-Fowler với dựa lưng v&agrave; tựa gối bằng cơ cấu tay quay uyển chuyển.</li>\n<li>Đầu giường v&agrave; ch&acirc;n giường bằng nhựa ABS chất lượng cao c&oacute; thể th&aacute;o rời &amp; ho&aacute;n đổi; đầu giường v&agrave; ch&acirc;n giường được trang bị kh&oacute;a an to&agrave;n v&agrave; cản lăn.</li>\n<li>Lan can b&ecirc;n c&oacute; thể gập lại. (Thanh ngang tr&ecirc;n bằng Aluminium, C&aacute;c thanh dọc SS v&agrave; ống dưới epoxy).</li>\n<li>Khung th&eacute;p nhẹ sơn phủ Epoxy v&agrave; 4 đầu được đục thủng.</li>\n<li>B&aacute;nh xe kh&ocirc;ng tiến ồn đường k&iacute;nh 125mm. (tất cả đều c&oacute; phanh ri&ecirc;ng).</li>\n<li>Chỗ cắm cho cọc truyền ở 2 b&ecirc;n giường.</li>\n<li>Cấu tr&uacute;c hỗ trợ sập giường.</li>\n</ul>', '<ul style="font-family: Arial, Helvetica, sans-serif; font-size: 12px;">\r\n<li>K&iacute;ch thước: 2150 x 900 x 520-720mm (DxRxC)</li>\r\n<li>Giường Elit Semi-Fowler với dựa lưng v&agrave; tựa gối bằng cơ cấu tay quay uyển chuyển.</li>\r\n<li>Đầu giường v&agrave; ch&acirc;n giường bằng nhựa ABS chất lượng cao c&oacute; thể th&aacute;o rời &amp; ho&aacute;n đổi; đầu giường v&agrave; ch&acirc;n giường được trang bị kh&oacute;a an to&agrave;n v&agrave; cản lăn.</li>\r\n<li>Lan can b&ecirc;n c&oacute; thể gập lại. (Thanh ngang tr&ecirc;n bằng Aluminium, C&aacute;c thanh dọc SS v&agrave; ống dưới epoxy).</li>\r\n<li>Khung th&eacute;p nhẹ sơn phủ Epoxy v&agrave; 4 đầu được đục thủng.</li>\r\n<li>B&aacute;nh xe kh&ocirc;ng tiến ồn đường k&iacute;nh 125mm. (tất cả đều c&oacute; phanh ri&ecirc;ng).</li>\r\n<li>Chỗ cắm cho cọc truyền ở 2 b&ecirc;n giường.</li>\r\n<li>Cấu tr&uacute;c hỗ trợ sập giường.</li>\r\n</ul>', 0, NULL, ''),
(6, 'GIƯỜNG HỒI SỨC CẤP CỨU', 5, 'M-700 plus', '20170510214205-giuong-benh-nhan.jpg', 'Malaysia', '<p>readmore</p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">N&acirc;ng lưng: khoảng 0&deg;-76&deg;; n&acirc;ng ch&acirc;n: 0&deg;-40&deg;</span></p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">N&acirc;ng hạ chiều cao: khoảng&nbsp; 460mm - 780mm</span></p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Điều chỉnh nghi&ecirc;ng xu&ocirc;i đầu giường: khoảng 0&deg;-16&deg; v&agrave; nghi&ecirc;ng ngược phần ch&acirc;n giường 0&deg;-16&deg;</span></p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">K&iacute;ch thước d&agrave;i x rộng của giường :&nbsp; khoảng&nbsp; 2165 mm&nbsp; x 1030 mm</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Độ d&agrave;y đệm: 125mm</span></p>', '<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">&nbsp; Seri giường bệnh nh&acirc;n Milenia l&agrave; sản phẩm của sự kết hợp ho&agrave;n hảo giữa nghi&ecirc;n cứu chuy&ecirc;n s&acirc;u về y học v&agrave; c&ocirc;ng nghệ hiện đại, đem lại thế hệ giường bệnh nh&acirc;n tiện lợi v&agrave; kinh tế.</span></p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">&nbsp; Thế hệ giường n&agrave;y sử dụng động cơ điện hiện đại gi&uacute;p điều khiển giường nhẹ nh&agrave;ng, tối thiểu h&oacute;a độ rung khi điều khiển &ndash; đ&aacute;p ứng ti&ecirc;u chuẩn y tế quốc tế.Mặc d&ugrave; được chế tạo với ti&ecirc;u ch&iacute; đem lại sự thoải m&aacute;i v&agrave; tiện dụng cho bệnh nh&acirc;n, giường cũng được thiết kế nhằm gi&uacute;p c&aacute;c điều dưỡng vi&ecirc;n sử dụng đơn giản.</span></p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">&nbsp; Thế hệ giường bệnh nh&acirc;n Milenia l&agrave; sự lựa chọn tối ưu cho c&aacute;c bệnh viện v&agrave; cho bệnh nh&acirc;n.Phần đầu giường v&agrave; ch&acirc;n giường được phủ ABS, với cơ chế kh&oacute;a m&oacute;c kh&ocirc;ng tạo gờ, c&oacute; thể th&aacute;o lắp dễ d&agrave;ng.C&oacute; thể điều chỉnh nghi&ecirc;ng xu&ocirc;i đầu giường, v&agrave; nghi&ecirc;ng ngược phần ch&acirc;n giường bằng l&ograve; xo kh&iacute;.</span></p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">&nbsp; To&agrave;n bộ giường được sơn phủ epoxy-polyester nhiệt h&oacute;a chống nứt v&agrave; chống trầy xước.Điện &aacute;p: 220V/50Hz.</span></p>', 0, 1, ''),
(7, 'Emergency Hospital Bed', 31, 'M-700 plus', '20170510221422-giuong-benh-nhan.jpg', 'Malaysia', '<p>readmore</p>\n<p>Backrest: about 0 &deg; -76 &deg;; Foot lift: 0 &deg; -40 &deg;</p>\n<p>Lifting height: about 460mm - 780mm</p>\n<p>Tilting down the bed: about 0 &deg; -16 &deg; and tilt the bed legs 0 &deg; -16 &deg;L</p>\n<p>ength x width of the bed: about 2165 mm x 1030 mm</p>\n<p>Buffer thickness: 125mm</p>', '<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">The Milenia bed series is the product of a complete combination of in-depth medical and technological research that benefits bed and economy. This stars use the basic mechanism of the sterling light, the minimum is rms when the control - the standard standards.</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Although built with standard mats and patient equipment, beds are also designed for simple use.</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;">The most bridge of the bridge Milenia is the choice of the privilege of the disease and for patients.</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;">The first wing and right knitted ABS, with non-padlocked lock, can be removed easily.Adjustable head, and torso with torso.All beds are epoxy-polyester coated with anti-cracking and anti-scratch.Voltage: 220V / 50Hz.</span></p>', 1, NULL, ''),
(8, 'MÁY RỬA PHIM X-QUANG TỰ ĐỘNG', 7, 'XP-9000', '20170510221948-may-rua-phim-tuu-dong_xp9000.jpg', 'Nhật Bản', '<div><span style="font-family: arial, helvetica, sans-serif; font-size: x-small;">readmore</span></div>\n<ul>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Truyền phim: Hệ thống truyền cuộn li&ecirc;n tục</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Tốc độ xử l&yacute; phim&nbsp;: 90 gi&acirc;y, 120 gi&acirc;y, 180 gi&acirc;y (c&oacute; thể chọn)</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Xử l&yacute; phim : Phim tấm, 6.1/2&rdquo; x 8.1/2&rdquo;~14&rdquo;x17&rdquo;</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Khả năng xử l&yacute;: 100 phim/giờ (tốc độ xử l&yacute;: 90 gi&acirc;y 10&rdquo;x12&rdquo;)</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">H&oacute;a chất :&nbsp;H&oacute;a chất cho m&aacute;y rửa phim X-quang tự động</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Thể t&iacute;ch th&ugrave;ng :Tr&aacute;ng (3.7L), H&atilde;m(2.4L), Rửa(2.3L)</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Nhiệt độ xử l&yacute; ti&ecirc;u chuẩn Tr&aacute;ng: 350C, 330C, 310C (c&oacute; thể chọn)</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Sấy: 600C&plusmn;50C (chế độ chờ: 450C)</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Kiểm so&aacute;t nhiệt độ Tr&aacute;ng: kiểm so&aacute;t nhiệt độ kh&ocirc;ng đổi bằng điện trở nhiệt</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Sấy: kiểm so&aacute;t nhiệt độ kh&ocirc;ng đổi bằng điện trở nhiệt</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Hệ thống bổ sung h&oacute;a chất: Hệ thống tự động bổ sung bằng cảm ứng phim</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Hệ thống trộn tr&aacute;ng: Bằng đĩa trộn xoay</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Hệ thống rửa&nbsp;: Hệ thống nước chảy (Hệ thống nước dự trữ c&oacute; thể chọn th&ecirc;m)</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Thiết bị an to&agrave;n: Hệ thống chống qu&aacute; nhiệt, M&aacute;y điều nhiệt an to&agrave;n c&agrave;i đặt lại bằng tay (Tr&aacute;ng/H&atilde;m/Sấy), Ngắt mạch</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Chế độ chờ: Tiết kiệm điện, chống m&ograve;n con lăn, duy tr&igrave; nhiệt độ sấy thấp, vận h&agrave;nh li&ecirc;n tiếp m&ocirc; tơ, bổ sung li&ecirc;n tiếp h&oacute;a chất.</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">M&ocirc;i trường&nbsp;: Nhiệt độ: 200C&plusmn;100C</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Độ ẩm: 30% ~ 80%</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Nhiệt độ nước: 50C hoặc hơn</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Nguồn: 1 pha, AC 220V/240V, 4.5A(110/120V, 9A), 50/60Hz</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">K&iacute;ch thước&nbsp;: 565 x 723 x 463mm (RxDxC)</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Trọng lượng tịnh: Khoảng 27.5kg</span></li>\n</ul>', '<ul>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Cho ph&eacute;p sử dụng phim v&agrave; h&oacute;a chất th&ocirc;ng thường cho việc xử l&yacute; trong 90 gi&acirc;y, kh&aacute;c biệt cho với c&aacute;c m&aacute;y rửa phim kh&aacute;c cần loại đặc biệt.</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">C&oacute; khả năng xử l&yacute; phim cao trong chế độ 90 gi&acirc;y.</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">C&oacute; khả năng thu h&igrave;nh ảnh phim tối ưu trong 90 gi&acirc;y, 120 gi&acirc;y hoặc 180 gi&acirc;y.</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Khả năng Tr&aacute;ng phim cao hơn, cho thời gian sử dụng li&ecirc;n tục d&agrave;i hơn.</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Bổ sung li&ecirc;n tiếp từng hồi h&oacute;a chất.</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Bổ sung giảm hiểu tối đa tiết kiệm h&oacute;a chất.</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Hộp đựng khu&ocirc;n đ&uacute;c nhựa &aacute;p lực v&agrave; hộp cho phim.</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">C&ocirc;ng tắc ăn khớp để tắt tất cả c&aacute;c mạch khi th&aacute;o hộp cho phim.</span></li>\r\n</ul>', 0, NULL, ''),
(9, 'MÁY RỬA PHIM X-QUANG TỰ ĐỘNG', 7, 'YP-33', '20170510222138-heejmay-rua-phim_yp33.jpg', 'Trung Quốc', '<div>readmore</div>\n<ul>\n<li>Cỡ phim rửa&nbsp;&nbsp;&nbsp;&nbsp; 3&rdquo; x 6&rdquo; ~ 4&rdquo; x 17&rdquo;</li>\n<li>Độ rộng phim lớn nhất&nbsp;&nbsp;&nbsp; 14&rdquo;</li>\n<li>Dung t&iacute;ch khay&nbsp; Khay hiện 6L, khay h&atilde;m 6L, khay nước rửa 6L</li>\n<li>Tốc độ rửa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 180 gi&acirc;y, 140 gi&acirc;y, 180 gi&acirc;y theo chọn th&ecirc;m (b&ecirc;n trong l&agrave; 80-360 gi&acirc;y)</li>\n<li>Nhiệt độ rửa&nbsp;&nbsp;&nbsp;&nbsp; 200C &ndash; 400C</li>\n<li>C&ocirc;ng suất rửa&nbsp;&nbsp; 80p/giờ, 120p/giờ</li>\n<li>C&ocirc;ng suất&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1.8kW</li>\n<li>Nguồn điện&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; AC220V, 50/60Hz</li>\n<li>Trọng lượng m&aacute;y&nbsp;&nbsp; 40kg</li>\n</ul>', '<ul>\r\n<li>Ứng dụng c&ocirc;ng nghệ Đức</li>\r\n<li>C&ocirc;ng nghệ sản phẩm: Vỏ m&aacute;y được l&agrave;m bằng nhựa nhựa chắc, bền, chống m&ograve;n.</li>\r\n<li>H&igrave;nh d&aacute;ng: nhỏ gọn, mẫu m&atilde; đẹp. Lấy phim thuận tiện</li>\r\n<li>Lắp đặt v&agrave; điều chỉnh: Chiếm &iacute;t diện t&iacute;ch, lắp đặt dễ d&agrave;ng, c&oacute; thể đặt trực tiếp tr&ecirc;n b&agrave;n.</li>\r\n<li>Khung đưa phim v&agrave;o: hệ thống đưa phim v&agrave;o thiết kế liền 1 khối, kh&ocirc;ng bị tắt, giắc film.</li>\r\n<li>S&acirc;y kh&ocirc; hiệu qủa cao: Hệ thống đường gi&oacute; c&oacute; thể thổi kh&ocirc; từng đi điểm nước tr&ecirc;n phim.</li>\r\n<li>Khay đưa phim v&agrave;o c&oacute; nắp: khay đưa phim v&agrave;o nhẹ v&agrave; c&oacute; thể đ&oacute;ng được, người sử dụng c&oacute; thể bật đ&egrave;n sau khi cho phim v&agrave;o. Thiết kế n&agrave;y gi&uacute;p l&agrave;m việc thuận tiện v&agrave; hiệu quả.</li>\r\n<li>Con lăn c&oacute; thể chịu được nhiệt cao, kh&ocirc;ng bị biến dạng v&agrave; sử dụng trong thời gian d&agrave;i.</li>\r\n<li>Mạch điều khiển: hệ thống l&agrave;m n&oacute;ng được điều khiển vi xử l&yacute;, tự động kiểm tra v&agrave; ph&aacute;t hiện phim, tự động b&ugrave; ho&aacute; chất</li>\r\n<li>Tiết kiệm năng lượng: Tự động chuyển về chế độ tiết kiệm năng lượng sau một thời gian khong hoạt động.</li>\r\n</ul>', 0, NULL, ''),
(10, 'MÁY X-QUANG THƯỜNG QUY', 6, 'YZ-200C', '20170510222608-x-quang_yz-200b.jpg', 'Trung Quốc', '<p>readmore</p>\r\n\r\n<table border="0" cellpadding="0" cellspacing="0">\r\n	<tbody>\r\n		<tr>\r\n			<th><span style="font-family:arial,helvetica,sans-serif; font-size:small">MỤC</span></th>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small"><strong>NỘI DUNG</strong></span></td>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small"><strong>CH&Uacute; TH&Iacute;CH</strong></span></td>\r\n		</tr>\r\n		<tr>\r\n			<th rowspan="3"><span style="font-family:arial,helvetica,sans-serif; font-size:small">Y&ecirc;u cầu nguồn điện</span></th>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">Dung lượng</span></td>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">32KVA, 2 pha</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">Điện &aacute;p tần số</span></td>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">380V/220V&plusmn;10%, 50Hz&plusmn;0.5Hz</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">Điện trở b&ecirc;n trong</span></td>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">0.75W(Khi 380 V)&nbsp; 0.25W(Khi 220 V).</span></td>\r\n		</tr>\r\n		<tr>\r\n			<th><span style="font-family:arial,helvetica,sans-serif; font-size:small">Nguồn</span></th>\r\n			<td>\r\n			<p><span style="font-family:arial,helvetica,sans-serif; font-size:small">Phạm vi điều chỉnh</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style="font-family:arial,helvetica,sans-serif; font-size:small">Khi 380V, 380+/-10%.</span></p>\r\n\r\n			<p><span style="font-family:arial,helvetica,sans-serif; font-size:small">Khi 220V 220V+/-10%.</span></p>\r\n\r\n			<p><span style="font-family:arial,helvetica,sans-serif; font-size:small">C&oacute; thể điều chỉnh li&ecirc;n tục.</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<th rowspan="3"><span style="font-family:arial,helvetica,sans-serif; font-size:small">Chụp phim</span></th>\r\n			<td>\r\n			<p><span style="font-family:arial,helvetica,sans-serif; font-size:small">Điện &aacute;p</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style="font-family:arial,helvetica,sans-serif; font-size:small">50-100KV&nbsp;<strong>(YZ &ndash; 200B),</strong>&nbsp;50-125KV&nbsp;<strong>(YZ- 300)</strong>, c&oacute; thể điều chỉnh&nbsp;li&ecirc;n tục.</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">D&ograve;ng điện</span></td>\r\n			<td>\r\n			<p><span style="font-family:arial,helvetica,sans-serif; font-size:small">Ti&ecirc;u cự nhỏ&nbsp;: 50mA 100mA.</span></p>\r\n\r\n			<p><span style="font-family:arial,helvetica,sans-serif; font-size:small">Ti&ecirc;u cự lớn: 50mA, 100mA, 200mA<strong>(YZ-200B),</strong></span></p>\r\n\r\n			<p><span style="font-family:arial,helvetica,sans-serif; font-size:small">300mA&nbsp;<strong>(YZ-300).</strong></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">Thời gian</span></td>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">0,04 &ndash;6,3 gi&acirc;y gồm 23 mức.</span></td>\r\n		</tr>\r\n		<tr>\r\n			<th rowspan="2"><span style="font-family:arial,helvetica,sans-serif; font-size:small">Biến &aacute;p cao nhất</span></th>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">Điện &aacute;p ra DC lớn nhất</span></td>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">100KV&nbsp;<strong>(YZ &ndash; 200B)</strong>, 125KV&nbsp;<strong>(YZ -300).</strong></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">D&ograve;ng điện ra DC lớn nhất</span></td>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">200mA&nbsp;<strong>(YZ &ndash; 200B)</strong>, 300mA&nbsp;<strong>(YZ -300)</strong>.</span></td>\r\n		</tr>\r\n		<tr>\r\n			<th><span style="font-family:arial,helvetica,sans-serif; font-size:small">B&oacute;ng ph&aacute;t tia X</span></th>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">Ti&ecirc;u điểm</span></td>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">Ti&ecirc;u điểm nhỏ: 1 x 1mm; Ti&ecirc;u điểm lớn: 2 x 2 mm.</span></td>\r\n		</tr>\r\n		<tr>\r\n			<th rowspan="3"><span style="font-family:arial,helvetica,sans-serif; font-size:small">B&agrave;n chụp</span></th>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">Mặt b&agrave;n</span></td>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">2000 x 710 x 700 mm (D&agrave;i x Rộng x Cao).</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">Phạm vi di chuyển mặt b&agrave;n</span></td>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">Dọc: 680mm, Ngang: 200mm.</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">S&agrave;ng tia</span></td>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">- H&agrave;nh tr&igrave;nh hướng dọc: &gt;= 500 mm.</span><br />\r\n			<span style="font-family:arial,helvetica,sans-serif; font-size:small">- Mật độ s&agrave;ng: N28.</span><br />\r\n			<span style="font-family:arial,helvetica,sans-serif; font-size:small">- Khoảng c&aacute;ch hội tụ: 100 cm.</span><br />\r\n			<span style="font-family:arial,helvetica,sans-serif; font-size:small">- Tỉ lệ Grid: r8.</span></td>\r\n		</tr>\r\n		<tr>\r\n			<th rowspan="4"><span style="font-family:arial,helvetica,sans-serif; font-size:small">Cột b&oacute;ng X-quang</span></th>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">Chuyển động hướng dọc</span></td>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">1800 mm hay 1600 mm.</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">Chuyển động l&ecirc;n xuống</span></td>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">650mm &ndash; 1800mm so với mặt đất.</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">Chuyển động trung t&acirc;m</span></td>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">quay tr&ograve;n quanh c&aacute;nh tay ngang: +/- 1800</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">Chuyển động quay tr&ograve;n đường trục b&oacute;ng đ&egrave;n tia X-quang</span></td>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">- 100 ~ 600 ~ +1200</span></td>\r\n		</tr>\r\n		<tr>\r\n			<th><span style="font-family:arial,helvetica,sans-serif; font-size:small">K&iacute;ch thước hộp Cassette đựng phim lớn nhất</span></th>\r\n			<td><span style="font-family:arial,helvetica,sans-serif; font-size:small">356x432mm(14&rsquo;&rsquo;x17&rsquo;&rsquo;).</span></td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '<ul>\r\n	<li><span style="font-family:arial,helvetica,sans-serif; font-size:small">B&oacute;ng đ&egrave;n X-quang Anode quay.</span></li>\r\n	<li><span style="font-family:arial,helvetica,sans-serif; font-size:small">Biến &aacute;p cao thế 1 pha, cả s&oacute;ng.</span></li>\r\n	<li><span style="font-family:arial,helvetica,sans-serif; font-size:small">C&oacute; cơ cấu điều chỉnh li&ecirc;n tục điện &aacute;p nguồn V, Kv chụp.</span></li>\r\n	<li><span style="font-family:arial,helvetica,sans-serif; font-size:small">C&oacute; thiết bị ổn &aacute;p sợi t&oacute;c b&oacute;ng đ&egrave;n, thiết bị b&ugrave; điện đảm bảo đầu ra ổn định, ch&iacute;nh x&aacute;c.</span></li>\r\n	<li><span style="font-family:arial,helvetica,sans-serif; font-size:small">C&ocirc;ng suất KV, mA, S ph&acirc;n th&agrave;nh nhiều mức, c&oacute; thiết bị bảo vệ kho&aacute; li&ecirc;n động.</span></li>\r\n	<li><span style="font-family:arial,helvetica,sans-serif; font-size:small">C&oacute; thiết bị định thời d&ograve;ng điện kỹ thuật số, ph&acirc;n mức theo hệ số ưu ti&ecirc;n R10, khống chế thời gian ch&iacute;nh x&aacute;c.</span></li>\r\n	<li><span style="font-family:arial,helvetica,sans-serif; font-size:small">B&agrave;n chụp, cột b&oacute;ng , s&agrave;ng rung lắp đặt th&agrave;nh một khối, kh&ocirc;ng c&oacute; đường ray tr&ecirc;n dưới.</span></li>\r\n	<li><span style="font-family:arial,helvetica,sans-serif; font-size:small">Điều chỉnh l&ecirc;n cao, xuống thấp, sang ngang.</span></li>\r\n</ul>\r\n', 0, 0, ''),
(11, 'MÁY X-QUANG KỸ THUẬT SỐ', 6, 'PEDS 600', '20170510222856-x-quang-tu-dong_peds600.png', 'Đức', '<div><span style="font-family: arial, helvetica, sans-serif; font-size: x-small;">readmore</span></div>\n<ul>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Hệ thống kĩ thuật sẵn s&agrave;ng tương th&iacute;ch ho&agrave;n to&agrave;n với DICOM.</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">C&oacute; thể sử dụng nhiều loại tấm cảm biến phẳng phi&ecirc;n bản kh&aacute;c nhau với định dạng ảnh 43x43 cm.</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">T&ugrave;y thuộc v&agrave;o c&aacute;c phi&ecirc;n bản tấm cảm biến phẳng, với thiết kế tương ứng, DR-Bucky l&agrave; một phần của hệ thống.</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Do thiết kế đặt biệt của PEDS 600, một tấm cảm biến phẳng sử dụng cho nhiều hướng, do vậy chỉ sử dụng một cột đỡ cho việc chụp theo chiều ngang v&agrave; chiều dọc.</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">B&oacute;ng X-quang v&agrave; tấm cảm biến phẳng được lắp tr&ecirc;n c&ugrave;ng hệ thống</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">H&igrave;nh ảnh chất lượng cao với liều lượng bức xạ l&agrave; tối thiểu.</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">&nbsp;Điều chỉnh li&ecirc;n tục li&ecirc;n tục khoản c&aacute;ch từ ti&ecirc;u điểm đến tấm cảm biến phẳng (FDA) tới 200 cm</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">C&aacute;nh tay đỡ b&oacute;ng c&oacute; thẻ xoay &plusmn;180&nbsp;v&agrave; di chuyển theo chiều dọc</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Chiều cao điều chỉnh từ 40 đến 220 cm</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">M&agrave;n h&igrave;nh cảm ứng d&ugrave;ng phần mềm CONAXX bao gồm c&aacute;c t&ugrave;y chọn n&acirc;ng cao m&ocirc;-đun</span></li>\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Độ ch&eacute;o ch&ugrave;m tia X-ray +/- 350</span></li>\n</ul>', '<p><span style="font-size: small; font-family: arial, helvetica, sans-serif;">- Thế hệ m&aacute;y PROVOTEC PEDS 600 ho&agrave;n to&agrave;n l&agrave; hệ thống chụp X-quang kỹ thuật số bao gồm bộ vi xử l&yacute; điều khiển nguồn cao PROVARIO 50/50 kW cũng như điều khiển c&aacute;nh tay đỡ lắp đặt tấm cảm biến phẳng &ndash; DR PROTEC RAPIXX 4343MDR. Bằng việc sử dụng phần mềm h&igrave;nh ảnh CONAXX được ph&aacute;t triển bới PROTEC, &aacute;p dụng tất cả c&aacute;c ứng dụng mới nhất của c&ocirc;ng nghệ kĩ thuật số, đ&atilde; tạo ra sự tối ưu trong việc chụp X-quang với chất lượng vượt trội trong v&ograve;ng v&agrave;i gi&acirc;y.</span></p>\r\n<p><span style="font-size: small; font-family: arial, helvetica, sans-serif;">- Do c&oacute; thiết kế nhỏ gọn, hệ thống trở n&ecirc;n l&iacute; tưởng trong việc chỉnh h&igrave;nh, phẫu thuật, chấn thương tại những nơi m&agrave; y&ecirc;u cầu về mặt kh&ocirc;ng gian trở n&ecirc;n quan trọng. Để đảm bảo một qui trinh l&agrave;m việc một c&aacute;ch nhanh ch&oacute;ng, điều khiển chụp tự động kỹ thuật số ( AEC) v&agrave; lập tr&igrave;nh giải phẫu l&agrave; một phần của chương trinh. Hệ thống lu&ocirc;n sẵn sang với c&aacute;c thế hệ tấm cảm biến phẳng PROTEC RAPIXX kh&aacute;c nhau, đồng thời cũng c&oacute; thể n&acirc;ng cấp ri&ecirc;ng rẽ l&ecirc;n tới phần mềm PACS ( phần mềm h&igrave;nh ảnh của PROPAXX).</span></p>', 0, NULL, ''),
(12, ' X-ray Digital', 6, 'PEDS 600', '20170510223807-x-quang-tu-dong_peds600.png', 'PROTEC - Đức', '<p>readmore</p>\n<p><span style="font-size: small; font-family: arial, helvetica, sans-serif;">The PEDS 600 is a state-of-the-art radiography system including a microprocessor controlled inverter generator from our PROVARIO HF series. Due to the compact design the system is ideal for any kind of examination techniques in orthopedic, surgical and traumatology applications where minimum space required is of vital importance. To ensure a fast workflow the digital automatic exposure control (AEC in classic and TOUCH version) and anatomical programmer (APR) are part of the package.</span></p>\n<p><span style="font-size: small; font-family: arial, helvetica, sans-serif;">According to individual user requirements, the PEDS 600 can be customized as an analog cassette-based system or a fully digital DR-unit. In case of a DR-configuration, the U-arm stand will be equipped with a PROTEC RAPIXX flatpanel detector. By using the CONAXX 2 image acquisition software from PROTEC, all advantages of latest digital technology are realized to full extend: Optimal workflow along with centralized control of the X-ray generator and the detector by CONAXX 2 provide diagnosable x-ray exposures of outstanding quality within a few seconds.</span></p>\n<p><span style="font-size: small; font-family: arial, helvetica, sans-serif;">The system is available with different PROTEC RAPIXXflatpanel versions and can, due to its modular set-up, also be upgraded individually with additional software options up to a PACS (e.g. PROPAXX<a href="http://www.protec-med.com/en/product/26-propaxx_software.html">&nbsp;</a>).</span></p>\n<p><span style="font-size: small; font-family: arial, helvetica, sans-serif;">As a recommendable accessory, we suggest any version of mobile PROGNOST XP series X-ray tables</span></p>', '<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">The PEDS 600 is a state-of-the-art radiography system including a microprocessor controlled inverter generator from our PROVARIO HF series. Due to the compact design the system is ideal for any kind of examination techniques in orthopedic, surgical and traumatology applications where minimum space required is of vital importance. To ensure a fast workflow the digital automatic exposure control (AEC in classic and TOUCH version) and anatomical programmer (APR) are part of the package.</span></p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">According to individual user requirements, the PEDS 600 can be customized as an analog cassette-based system or a fully digital DR-unit. In case of a DR-configuration, the U-arm stand will be equipped with a PROTEC RAPIXX flatpanel detector. By using the CONAXX 2 image acquisition software from PROTEC, all advantages of latest digital technology are realized to full extend: Optimal workflow along with centralized control of the X-ray generator and the detector by CONAXX 2 provide diagnosable x-ray exposures of outstanding quality within a few seconds.</span></p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">The system is available with different PROTEC RAPIXXflatpanel versions and can, due to its modular set-up, also be upgraded individually with additional software options up to a PACS (e.g. PROPAXX<a href="http://www.protec-med.com/en/product/26-propaxx_software.html">&nbsp;</a>).</span></p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">As a recommendable accessory, we suggest any version of mobile PROGNOST XP series X-ray tables.</span></p>', 0, NULL, ''),
(14, 'Máy sắc thuốc 24 ấm', 37, 'MST-24', '20170617140847-catolo-1--sac-thuoc.jpg', 'Việt Nam', 'Viet-American Medical Technology Company Limited (vietmymedical) was established in 2016 and engages in the trading of medical equipment and supplies health services. Starting from a small model company, Vietmymedical has now grown into a company with more than 100 employees focused on the field of medical equipment and medical consumables. \nWe understand that medical device business is a special business that directly affects human health and lives. Therefore, right from the establishment, the management of Vietmymedical Company has determined the business motto is to provide quality products and quality service of technical support services including warranty service, Repair as well as user training is a top priority. This is one of the keys to Vietmymedical''s success as it is today and will always be our guide in the future. \n\n      In addition to providing customers with high quality products and prestige, Vietmymedical also offers comprehensive hospital consultancy solutions. Being aware of the differences in the needs and equipment of each hospital, Vietmymedical offers a wide selection of product lines to suit both financial and pre-use needs. Eyes but also easy to extend future upgrades.<p>readmore</p>\n<p><span style="font-size: small; font-family: arial, helvetica, sans-serif;">- Hệ thống tủ điện 3 chế độ</span></p>\n<p><span style="font-size: small; font-family: arial, helvetica, sans-serif;">- Nguồn điện 220v</span></p>\n<p><span style="font-size: small; font-family: arial, helvetica, sans-serif;">- K&iacute;ch thước 2.5x0.6xx1.2</span></p>\n<p><span style="font-size: small; font-family: arial, helvetica, sans-serif;">- 24 ấm</span></p>', '<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Cấu tạo</strong></span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Hệ thống c&oacute; nắp đậy k&iacute;n với 3 lớp gi&uacute;p m&aacute;y giữ nhiệt nhanh s&ocirc;i, tiết kiệm điện v&agrave; giảm thời gian hoạt động</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Thiết bị c&oacute; 3 lớp, 2 lớp vỏ trong v&agrave; ngo&agrave;i bằng inox, lớp giữa bảo &ocirc;n dầy, đảm bảo kh&ocirc;ng mất nhiệt trong qu&aacute; tr&igrave;nh s&ocirc;i, tr&aacute;nh g&acirc;y bỏng trong qu&aacute; tr&igrave;nh sử dụng</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Thiết bị được chế tạo ho&agrave;n to&agrave;n bằng inox chuy&ecirc;n d&ugrave;ng c&oacute; độ bền v&agrave; sức chịu đựng cao, đảm bảo an to&agrave;n v&agrave; tuổi thọ cao.</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Hệ thống c&oacute; nắp đậy k&iacute;n</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Nguy&ecirc;n l&yacute; hoạt động</strong>:</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- M&aacute;y sắc thuốc theo nguy&ecirc;n l&yacute; d&ugrave;ng dầu truyền nhiệt để đun thuốc , thanh sợi đốt kh&ocirc;ng tiếp x&uacute;c trực tiếp với thuốc, nhiệt độ s&ocirc;i c&agrave;i đặt được. V&igrave; vậy chất lượng thuốc đảm bảo kh&ocirc;ng bị ch&aacute;y</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Cơ chế vận h&agrave;nh :</strong></span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Kiểm tra nguồn điện v&agrave;o m&aacute;y</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Cho thuốc v&agrave; nước v&agrave;o ấm, c&agrave;i đặt nhiệt độ v&agrave; thời gian sắc, bật c&ocirc;ng tắc chạy m&aacute;y</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- C&oacute; thể lựa chọn hoạt động theo thời gian c&agrave;i đặt hoặc li&ecirc;n tục</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- C&oacute; đ&egrave;n b&aacute;o theo d&otilde;i qu&aacute; tr&igrave;nh m&aacute;y đang chạy, tăng c&ocirc;ng suất bằng điều khiển cảm biến</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Thời gian l&agrave;m việc c&oacute; thể c&agrave;i đặt đến 99h đ&aacute;p ứng đầy đủ c&aacute;c nhu cầu</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Hệ thống l&agrave;m việc tự động m&aacute;y sẽ tự động đốt v&agrave; khống chế nhiệt độ trong phạm vi cho ph&eacute;p, m&aacute;y sẽ tự dừng khi hết thời gian c&agrave;i đặt.</span></p>', 0, 1, ''),
(15, 'Bồn rửa tay phẫu thuật tự động', 38, 'BR-VM02', '20170617142936-bon-rua-tay-3.jpg', 'Việt Nam', '<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"> Viet-American Medical Technology Company Limited (vietmymedical) was established in 2016 and engages in the trading of medical equipment and supplies health services. Starting from a small model company, Vietmymedical has now grown into a company with more than 100 employees focused on the field of medical equipment and medical consumables. \nWe understand that medical device business is a special business that directly affects human health and lives. Therefore, right from the establishment, the management of Vietmymedical Company has determined the business motto is to provide quality products and quality service of technical support services including warranty service, Repair as well as user training is a top priority. This is one of the keys to Vietmymedical''s success as it is today and will always be our guide in the future. \n\n      In addition to providing customers with high quality products and prestige, Vietmymedical also offers comprehensive hospital consultancy solutions. Being aware of the differences in the needs and equipment of each hospital, Vietmymedical offers a wide selection of product lines to suit both financial and pre-use needs. Eyes but also easy to extend future upgrades.</span></p>\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">readmore</span></p>\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Cấu h&igrave;nh ti&ecirc;u chuẩn:</strong></span></p>\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Bồn rửa ch&iacute;nh: 01 chiếc</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;"> V&ograve;i nước h&igrave;nh cổ vịt: 02 chiếc</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;"> Bộ khung gắn bằng th&eacute;p kh&ocirc;ng rỉ: 01 chiếc</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;"> Cảm biến hồng ngoại t&iacute;ch hợp v&agrave;o bồn rửa: 02 chiếc</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;"> Valve điện tử trong hộp điều khiển: 02 chiếc</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;"> V&ograve;i kết nối nước bằng vật liệu HDPE (chống chlorine): 02 chiếc</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;"> Siphon đường k&iacute;nh 40mm: 01 chiếc</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;"> Bộ tiền lọc v&agrave; l&otilde;i lọc: 01 bộ</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;"> Bộ xử l&yacute; nước bằng tia cực t&iacute;m: 01 bộ</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;"> Bộ lọc cuối (lắp ở v&ograve;i nước, d&ugrave;ng để lọc vi sinh vật, vi khuẩn trong nước): 01 bộ</span></p>\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Th&ocirc;ng số kỹ thuật:</strong></span></p>\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">K&iacute;ch thước: d&agrave;i 1500mm, s&acirc;u 570mm, cao 745mm</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;"> V&ograve;i nước tự động mở khi cảm biến nhận dạng tay đưa v&agrave;o gần.</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;"> Hệ thống c&oacute; thể tự đ&ocirc;ng xả thải sau 6/12 giờ hoặc kh&ocirc;ng xả sau lần sử dụng cuối.</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;"> Tự động xả, d&ograve; khoảng c&aacute;ch v&agrave; chu tr&igrave;nh xả được dễ d&agrave;ng c&agrave;i đặt bằng điều khiển tự xa.</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;"> Hộp solenoid dễ d&agrave;ng gỡ ra để bảo tr&igrave;.</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;"> Trọng lượng: 19Kg</span></p>', '<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><span style="white-space: pre;"> </span>Bồn rửa tay hai v&ograve;i được đ&uacute;ng nguy&ecirc;n khối m&agrave; kh&ocirc;ng c&oacute; c&aacute;c g&oacute;c cạnh sắc như c&aacute;c bồn rửa th&ocirc;ng thường bằng th&eacute;p kh&ocirc;ng rỉ. Vật liệu bọc v&agrave; h&igrave;nh th&aacute;i trơn tru gi&uacute;p việc vệ sinh cực dễ d&agrave;ng.</span><br /><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><span style="white-space: pre;"> </span>Được l&agrave;m bằng polyester cường lực với lớp phủ gelcoat đạt ti&ecirc;u chuẩn vệ sinh (Iso NPG), lớp phủ n&agrave;y của ch&uacute;ng t&ocirc;i đảm bảo hạn chế mảng b&aacute;m v&agrave; nước đọng tr&ecirc;n bề mặt. Qua đ&oacute;, bồn rửa chống sự sinh s&ocirc;i của vi khuẩn v&agrave; sự nhiễm khuẩn với thiết kế s&aacute;ng trắng.</span></p>\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><span style="white-space: pre;"> </span>Ba ưu điểm ngắn gọn:</span></p>\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"> - Dễ d&agrave;ng vệ sinh</span></p>\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"> - Bền bỉ</span></p>\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"> - Thiết kế kh&ocirc;ng chạm đến s&agrave;n, gi&uacute;p dễ d&agrave;ng lau s&agrave;n</span></p>\n<ul>\n</ul>', 0, 1, '');
INSERT INTO `sanpham` (`idsp`, `tensp`, `iddm`, `masp`, `img`, `hangsp`, `thongtin`, `baiviet`, `ngonngu`, `noibat`, `link_title`) VALUES
(18, 'MÁY SIÊU ÂM ĐEN TRẮNG DI ĐỘNG', 0, 'DP-8500V', '20170625090048-20160128100344_dp-8500.jpg', 'Trung Quốc', '<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>C&ocirc;ng nghệ kỹ thuật số</strong><strong>:</strong><strong>&nbsp;</strong></span></p>\r\n<ul>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">H&igrave;nh ảnh h&ograve;a &acirc;m m&ocirc; (THI)</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">H&igrave;nh ảnh m&ocirc; đặc biệt (TSI)</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Định dạng ch&ugrave;m kỹ thuật số (DBF)</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Ti&ecirc;u cự động (DRF)</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Qu&eacute;t tần số động (DFS)</span></li>\r\n</ul>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Độ s&acirc;u thăm kh&aacute;m</strong><strong>:</strong>tới 302mm (phụ thuộc đầu d&ograve;)</span></p>\r\n<p>readmore</p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Chế độ h&igrave;nh ảnh</strong><strong>:</strong><strong> </strong>B, 2B, 4B, M, B+M</span></p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Thang x&aacute;m</strong><strong>:</strong><strong> </strong>256</span></p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Tần số đầu d&ograve;</strong><strong>:</strong><strong> </strong>2- 10MHz, tới 6 tần số</span></p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Kết nối đầu đ&ograve;</strong><strong>:</strong><strong> </strong>2 (ti&ecirc;u chuẩn)</span></p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>C&ocirc;ng nghệ kỹ thuật số</strong><strong>:</strong><strong> </strong></span></p>\r\n<ul style="list-style-type: square;">\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">H&igrave;nh ảnh h&ograve;a &acirc;m m&ocirc; (THI)</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">H&igrave;nh ảnh m&ocirc; đặc biệt (TSI)</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Định dạng ch&ugrave;m kỹ thuật số (DBF)</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Ti&ecirc;u cự động (DRF)</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Qu&eacute;t tần số động (DFS)</span></li>\r\n</ul>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Độ s&acirc;u thăm kh&aacute;m</strong><strong>:</strong>tới 302mm (phụ thuộc đầu d&ograve;)</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Cổng ngoại vi</strong><strong>: </strong>Video, S-video, VGA, cổng USB, c&ocirc;ng tắc đạp ch&acirc;n, cổng Ethernet cho DICOM 3.0 (chọn th&ecirc;m)</span></p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Nguồn</strong><strong>:</strong><strong> </strong>100 ~ 240AC&plusmn;10%, 50/60Hz</span></p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>K&iacute;ch thước</strong><strong>:</strong><strong> </strong>1225 x 705 x 500mm&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Trọng lượng tịnh</strong><strong>:</strong><strong> </strong>~ 42kg</span></p>', '<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Xử l&yacute; h&igrave;nh ảnh</strong><strong>&nbsp;</strong></span></p>\r\n<table border="0" cellspacing="0" cellpadding="0">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Tiền xử l&yacute; h&igrave;nh ảnh</strong><strong>&nbsp;</strong></span></p>\r\n</td>\r\n<td width="427">\r\n<ul>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">TGC 8 dải</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Xử l&yacute; h&igrave;nh ảnh (IP)</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Dải động</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">L&agrave;m nổi đường bi&ecirc;n</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Tương quan khung</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Lựa chọn g&oacute;c qu&eacute;t</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Độ ph&acirc;n giải cao/   tốc độ khung cao</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">C&ocirc;ng suất &acirc;m</span></li>\r\n</ul>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Hậu xử l&yacute; h&igrave;nh ảnh</strong><strong>&nbsp;</strong></span></p>\r\n</td>\r\n<td width="427">\r\n<ul>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Thang x&aacute;m</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Đổi trắng/đen</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Quay h&igrave;nh tr&aacute;i/   phải</span></li>\r\n<li><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Lật h&igrave;nh l&ecirc;n/xuống</span></li>\r\n</ul>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<table border="0" cellspacing="0" cellpadding="0">\r\n<tbody>\r\n<tr>\r\n<td width="151">\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Cine   loop</strong><strong>&nbsp;</strong></span></p>\r\n</td>\r\n<td width="425">\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">bộ nhớ 1500 khung   h&igrave;nh</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width="151">\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Bộ nhớ   media</strong><strong>&nbsp;</strong></span></p>\r\n</td>\r\n<td width="425">\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Bộ nhớ ti&ecirc;u chuẩn   400M, c&oacute; thể chọn ổ lưu trữ 320G, DVD-RW</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width="151">\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Zoom</strong><strong>&nbsp;</strong></span></p>\r\n</td>\r\n<td width="425">\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Ph&oacute;ng h&igrave;nh to&agrave;n   cảnh thời gian thực hoặc dừng h&igrave;nh &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<table border="0" cellspacing="0" cellpadding="0">\r\n<tbody>\r\n<tr>\r\n<td width="151">\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>Chế độ   B</strong><strong>&nbsp;</strong></span></p>\r\n</td>\r\n<td width="425">\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Khoảng c&aacute;ch, chu   vi, diện t&iacute;ch, thể t&iacute;ch, g&oacute;c, NT, đường cong tăng trưởng của b&agrave;o thai, thể   t&iacute;ch b&agrave;ng quang, khối lượng tuyến tiền liệt, thể t&iacute;ch tử cung v.v&hellip;</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width="151">\r\n<p><span style="font-family: arial, helvetica, sans-serif; font-size: small;